    // }
// ----------------------------------------------------------------
    // return (
    //     () => {
    //         clearDetail() //para que se limpie el estado  esto es del useEffect
    //     }
    // )
    useEffect(() => { 
        dispatch(getGames()); 
    }, [dispatch]);

    const itemsList = allGames.map((item, index) => {
        return <li key={item.id}>{item}</li>
    })

    return (
        <div>
            <h1>HOME</h1>
            <NavBar handleCange={handleChange} handleSummit={handleSubmit} />
            <Paginado currentPage={0} items={items} nextHandler={nextHandler} prevHandler={prevHandler} />
            {/* <Cards allGames={allGames}/> va a ser el array con todos mis juegos     */}
            
        </div>
    )
};

export default Home;
