import { useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';
import { postGame } from '../../Redux/Actions';
const Form = () => {
    const dispatch = useDispatch()
    const [form, setForm] = useState({

    })
    const [name ,setName] = useState('');
    const [platform , setPlatform] = useState([]);
    const [genres, setGenres] = useState([]);
    const [rating, setRating] = useState('');
    const [image, setImage] = useState('');
    const [updated, setUpdated] = useState('');
    const [description, setDescription] = useState('');    


const handlerName = (event) => {
    setName(event.target.value);
};
const handlerPlatform = (event) => {
    setPlatform(event.target.value.split(','));
};
const handlerGenres = (event) => {
    setGenres(event.target.value.split(','));
};
const handlerRating = (event) => {
    setRating(event.target.value);
};
const handlerImage = (event) => {
    setImage(event.target.value);
};
const handlerUpdated = (event) => {
    setUpdated(event.target.value);
};
 const handlerDescription = (event) => {
    setDescription(event.target.value);
 ;}   

//  useEffect(()=> {
//     dispatch(postGame(
//         name,
//         // platform,
//         // genres,
//         // rating,
//         // image,
//         // updated,
//         // description,
//         ))  
//  }, [name])

    return (
        <div> 
            <h1>Crear Videojuego</h1>
            <form>
                <label htmlFor="name">Nombre: </label>
                <input id='name' value={name} type="text" name='name' placeholder="Escribir nombre" onChange={handlerName} />
                <hr />
                <label htmlFor="platform">Plataformas: </label>
                <input id='platform' value={platform} type="text" name='platform' placeholder="Inserte las plataformas de jugabilidad" onChange={handlerPlatform} />
                <hr />
                <label htmlFor="genres">Géneros: </label>
                <input id='genres' value={genres} type="text" name='genres' placeholder="Inserte los géneros" onChange={handlerGenres} />
                <hr />
                <label htmlFor="rating">Rating: </label>
                <input id='rating' value={rating} type="text" name='rating' placeholder="Puntúe el juego" onChange={handlerRating} />
                <hr />
                <label htmlFor="image">Imagen: </label>
                <input id='image' value={image} type="text" name='image' placeholder="Inserte link de la imagen" onChange={handlerImage} />
                <hr />
                <label htmlFor="updated">Fecha: </label>
                <input id='updated' value={updated} type="text" name='updated' placeholder="Coloque la fecha" onChange={handlerUpdated} />
                <hr />
                <label htmlFor="description">Descripción:</label>
                <input id='description' value={description} type="text" name='description' placeholder="Elabore una breve descripción del juego" onChange={handlerDescription} />
                <button>Crear Juego</button>
            </form>
        </div>
    )
};

export default Form;
