import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import { getGames, getByName } from '../../Redux/Actions'
// import Cards from '../../Components/Cards/cards';
import NavBar from '../../Components/NavBar/nav';
import Paginado from '../../Components/Paginado/pajinado'

function Home () {
    const itemsPerPage = 15
    const dispatch = useDispatch();
    const allGames = useSelector((state) => state.allGames); 
    const [searchString, setSearchString] = useState('');
    const [items, setItems] = useState({
        item: allGames.slice(0, itemsPerPage),
        
    });
    
    
    
    // const items = [allGames.slice(0, itemsPerPage)];
    const [currentPage, setCurrentPage] = useState(0)
    
    
       
    console.log(items.item);
    //* filtro sobre la BD

    function handleChange (e) {
        e.preventDefault();
        setSearchString(e.target.value)  
    };
    
    //* filtro sobre el estado
    function handleSubmit (e) {
        e.preventDefault();
        dispatch(getByName(searchString))
    };

    

    const nextHandler = () => {
        const totalElements = allGames.length;

        const nextPage = currentPage + 1;

        const firstIndex = nextPage * itemsPerPage;

        if (firstIndex === totalElements) return;


        
        setCurrentPage(nextPage);
    };

    const prevHandler = () => {
        const prevPage = currentPage - 1;
        
        if (prevPage < 0) return; 

        const firstIndex = prevPage * itemsPerPage;

        setCurrentPage(prevPage);
        console.log('prev');
    };

    
    // const handlerClick = (id) => {};
  
    // const [filtrado, setFiltrado] = useState(allGames);
    
    //     const filtrado = allGames.filter((game) => {
    //         game.name.include(searchString)
            
    //     });
    //     setFiltrado(filtrado);
    // }
// ----------------------------------------------------------------
    // return (
    //     () => {
    //         clearDetail() //para que se limpie el estado  esto es del useEffect
    //     }
    // )
    useEffect(() => { 
        dispatch(getGames()); 
    }, [dispatch]);

    const itemsList = items.item.value.map((item, index) => {
        return <li key={item.id}>{item}</li>
    })

    return (
        <div>
            <h1>HOME</h1>

            <NavBar handleCange={handleChange} handleSummit={handleSubmit} />
            <Paginado currentPage={currentPage} items={items} nextHandler={nextHandler} prevHandler={prevHandler} />
            {/* <Cards allGames={allGames}/> va a ser el array con todos mis juegos     */}
            
        </div>
    )
};

export default Home;
