import { useState, useEffect } from 'react';
const Form = () => {
    const [name ,setName] = useState('');
    const [platform , setPlatform] = useState('');
    const [genres, setGenres] = useState('');
    const [rating, setRating] = useState('');
    const [image, setImage] = useState('');
    const [updated, setUpdated] = useState('');
    const [description, setDescription] = useState('');    


    return (
        <div>
            <h1>Crear Videojuego</h1>
            <form>
                <label htmlFor="">Nombre: </label>
                <input type="text" name='name' placeholder="Escribir nombre" />
                <hr />
                <label htmlFor="">Plataformas: </label>
                <input type="text" name='platform' placeholder="Inserte las plataformas de jugabilidad" />
                <hr />
                <label htmlFor="">Géneros: </label>
                <input type="text" name='genres' placeholder="Inserte los géneros" />
                <hr />
                <label htmlFor="">Rating: </label>
                <input type="text" name='rating' placeholder="Puntúe el juego" />
                <hr />
                <label htmlFor="">Imagen: </label>
                <input type="text" name='image' placeholder="Inserte link de la imagen" />
                <hr />
                <label htmlFor="">Fecha: </label>
                <input type="text" name='updated' placeholder="Coloque la fecha" />
                <hr />
                <label htmlFor="">Descripción:</label>
                <input type="text" name='description' placeholder="Elabore una breve descripción del juego" />
            </form>
        </div>
    )
};

export default Form;
