import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import { getGames, getGenres } from '../../Redux/Actions'
import Cards from '../../Components/Cards/cards';
import NavBar from '../../Components/NavBar/nav';
import Filter from '../../Components/Filter/Filter';



function Home () {
    const dispatch = useDispatch();
    const allGames = useSelector((state) => state.allGames); 
    const [totalGames, setTotalGames] = useState([])
    const [filtros, setFiltros] = useState({
        orden: '',
        origen: ''
    })
   const [filterGames, setFilterGames] = useState({});

  
    //* filtro sobre la BD
 
    // const handlerClick = (id) => {};
  
    // const [filtrado, setFiltrado] = useState(allGames);
    
    //     const filtrado = allGames.filter((game) => {
    //         game.name.include(searchString)
            
    //     });
    //     setFiltrado(filtrado);
    // }
// ----------------------------------------------------------------
    // return (
    //     () => {
    //         clearDetail() //para que se limpie el estado  esto es del useEffect
    //     }
    // )

    useEffect(() => { 
        setTotalGames(allGames)
        dispatch(getGames()); 
    }, []);

    useEffect(() => {
        dispatch(getGenres());
    }, [])

    useEffect(()=> {
        let filterGames = [...allGames];
        
        if (filtros.orden === 'A') {
            filterGames.sort((a, b) => a.name.local)
        }
    })

    return (
        <div>
            <h1>HOME</h1>

            <NavBar/>
            <h2>Galer√≠a</h2>
            <Filter allGames={allGames}/>
            <Cards allGames={allGames}/>     
            
        </div>
    )
};

export default Home;
