import { Route } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import { getGames, clearDetail } from '../../Redux/Actions'
import Cards from '../../Components/Cards/cards';
import NavBar from '../../Components/NavBar/nav';

const Home = () => {
    const dispatch = useDispatch();
    const allGames = useSelector((state) => state.allGames); 
    const [filtrado, setFiltrado] = useState(allGames);
    const [searchString, setSearchString] = useState('');
    
    const handleChange = (e) => {
        e.preventDefault();
        searchString(e.target.value)
        
    };
    const handleSubmit = (e) => {
        e.preventDefault();
        const filtrado = allGames.filter((game) => {
            game.name.include(searchString)
            
        });
        setFiltrado(filtrado);
    }

    useEffect(() => { 
        dispatch(getGames()); 
        return (
            () => {
                clearDetail() //para que se limpie el estado 
            }
        )
    }, [dispatch])
    return (
        <div>
            <h1>HOME</h1>
          
            <NavBar handleCange={handleChange} handleSummit={handleSubmit} />
            <Cards allGames={filtrado}/> {/* va a ser el aaray ocn todos mis juegos */}
        </div>
    )
};

export default Home;
