import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import { getGames, getGenres } from '../../Redux/Actions'
import Cards from '../../Components/Cards/cards';
import NavBar from '../../Components/NavBar/nav';
import Filter from '../../Components/Filter/Filter';
import style from './home.module.css';
import filteredGames from '../../Components/Filter/Filter';


function Home () {
    const allGames = useSelector((state) => state.allGames); 
    const allGenres = useSelector((state) => state.allGenres);
    const [filtrado, setFiltrado] = useState('');
    const [totalGames, setTotalGames] = useState([])
    const dispatch = useDispatch();
    
    
    useEffect(() => { 
        setTotalGames(allGames)
        dispatch(getGames()); 
    }, []);
    
    useEffect(() => {
        dispatch(getGenres());
    }, [])
    const handleChange = (e) => {
        setTotalGames(e.target.value)
    };
    
    const filteredGames = filtrado
    ? allGames.filter((game) => game.genres?.includes(filtrado))
    : allGames;
    console.log(filtrado);
    
    return (
        <div >
            <h1 className={style.h1} >HOME</h1>
            <Filter className={style.filters} allGames={allGames} />
            <label className={style.label} htmlFor="genres">Filtrar por GÃ©nero: </label>
                            <select onChange={handleChange} value={filtrado}>
                                <option value=''>
                                    Select
                                </option>
                                {allGenres?.map((gen) => {    
                                    return (
                                    <option value= {gen.name} key={gen.name}>
                                        {gen.name}
                                    </option>
                                    )
                                })
                            }
                         </select>
            <NavBar/>
            <Cards allGames={allGames}/>     
            
        </div>
    )
};

export default Home;
