    //     }
    // )

    useEffect(() => { 
        setTotalGames(allGames)
        dispatch(getGames()); 
    }, [allGames]);


    return (
        <div>
            <h1>HOME</h1>

            <NavBar/>
            <h2>Galer√≠a</h2>
            <Cards allGames={allGames} />     
            
        </div>
    )
};

export default Home;
