import { useState } from "react";
export function validateName (event) {
    const [form, setForm] = useState({});
    const [error, setError] = useState({
        name:'',
        platform: '',
        genres: '',
        rating: '',
        image: '',
        updated: '',
        description: '',
    });
    
    if (event.target.value !== '') {
        if (/^[a-zA-Z0-9\s]+$/.test(form.name)) {
            return '';
        } else {
            return '*No debe contener signos'
        }
    }
};

