    //     const filtrado = allGames.filter((game) => {
    //         game.name.include(searchString)
            
    //     });
    //     setFiltrado(filtrado);
    // }
// ----------------------------------------------------------------
    // return (
    //     () => {
    //         clearDetail() //para que se limpie el estado  esto es del useEffect
    //     }
    // )
    useEffect(() => { 
        dispatch(getGames()); 
    }, [dispatch]);

    const itemsList = allGames.map((item, index) => {
        return <li key={item.id}>{item}</li>
    })

    return (
        <div>
            <h1>HOME</h1>
            <NavBar handleCange={handleChange} handleSummit={handleSubmit} />
            <h3>Pagina: {currentPage}</h3>
            <button onClick={prevHandler} >Prev</button>
            <button onClick={nextHandler} >Next</button>
            <ul>
            <Cards allGames={itemsList}/> {/* va a ser el array con todos mis juegos */}    
            </ul>
        </div>
    )
};

export default Home;
