import { useSelector, useDispatch} from 'react-redux'; 
import { useState } from 'react';
import { filter } from '../../Redux/Actions';
import style from './Filter.mo'
const Filter = ({allGames}) => {
    const allGenres = useSelector((state) => state.allGenres);
   
    const dispatch = useDispatch();
    
    const [filtrado, setFiltrado] = useState(allGames);
    
    
     const handleFilter = (event) => {
         const gamefilter = allGames.filter((game) => {
            console.log(allGames);
                 game.genres.map ((genre) => {
                     return genre.name ===  event.target.value
                 })
                 
             });
            
             setFiltrado(gamefilter);
             return filtrado;
     }

     const hancleOrder = () => {
            let filterGames = [...allGames];

             if (filtrado.orden === 'A') {
                 filterGames.sort((a, b) => a.name.localeCompare(b.name))
             }
             if (filtrado.orden === 'D') {
                 filterGames.sort((a, b) => b.name.localeCompare(a.name))
             }
             
             return setFiltrado(filterGames)
     }
        
    // const handleFilterChange = (event) => {
    //     dispatch(filter(event.target.value))
    // };  
    
    
    return (
        <div>
            <label htmlFor="genres">Filtrar por GÃ©nero: </label>
                    <select onChange={handleFilter}>
                    <option>
                        Select
                    </option>
                    {allGenres?.map((gen) => {
                        return (
                         <option value= {gen.id} key={gen.name}>
                             {gen.name}
                         </option>
                     )
                 })}
                 </select>
                 <label htmlFor="orden">Filtrar por Orden: </label>
                <select >
                <option>Select</option>
                <option value="nombre" onClick={hancleOrder}>Nombre</option>
                <option value="rating">Rating</option>
                
                </select>
        </div>
    )
};

export default Filter;
