import { useState, useEffect } from 'react';
const Form = () => {
    const [name ,setName] = useState('');
    const [platform , setPlatform] = useState([]);
    const [genres, setGenres] = useState([]);
    const [rating, setRating] = useState('');
    const [image, setImage] = useState('');
    const [updated, setUpdated] = useState('');
    const [description, setDescription] = useState('');    

const handlerName = (event) => {
    setName(event.target.value);
};
const handlerPlatform = (event) => {
    setPlatform(event.target.value);
};
const handlerGenres = (event) => {
    setGenres(event.target.value);
};
const handlerRating = (event) => {
    setRating(event.target.value);
};
const handlerImage = (event) => {
    setImage(event.target.value);
};
const handlerUpdated = (event) => {
    setUpdated(event.target.value);
};
 const handlerDescription = (event) => {
    setDescription(event.target.value);
 ;}   

    return (
        <div>
            <h1>Crear Videojuego</h1>
            <form>
                <label htmlFor="">Nombre: </label>
                <input value={name} type="text" name='name' placeholder="Escribir nombre" />
                <hr />
                <label htmlFor="">Plataformas: </label>
                <input value={platform} type="text" name='platform' placeholder="Inserte las plataformas de jugabilidad" />
                <hr />
                <label htmlFor="">Géneros: </label>
                <input value={genres} type="text" name='genres' placeholder="Inserte los géneros" />
                <hr />
                <label htmlFor="">Rating: </label>
                <input value={rating} type="text" name='rating' placeholder="Puntúe el juego" />
                <hr />
                <label htmlFor="">Imagen: </label>
                <input value={image} type="text" name='image' placeholder="Inserte link de la imagen" />
                <hr />
                <label htmlFor="">Fecha: </label>
                <input value={updated} type="text" name='updated' placeholder="Coloque la fecha" />
                <hr />
                <label htmlFor="">Descripción:</label>
                <input value={description} type="text" name='description' placeholder="Elabore una breve descripción del juego" />
            </form>
        </div>
    )
};

export default Form;
